import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../../context/UserContext';
import { GameContext } from '../../context/GameContext';
import './style.css';

const Navbar = ({ darkMode, toggleDarkMode, gameId }) => {
  const { user, updateUser } = useContext(UserContext);
  const { changeTeam, game, handleTeamChange } = useContext(GameContext);
  
  const [showSettings, setShowSettings] = useState(false);
  const [newUsername, setNewUsername] = useState(user?.username || '');
  const [isChangingTeam, setIsChangingTeam] = useState(false);
  
  // Update username when user changes
  useEffect(() => {
    if (user?.username) {
      setNewUsername(user.username);
    }
  }, [user]);
  
  const handleUsernameChange = (e) => {
    e.preventDefault();
    if (newUsername.trim()) {
      updateUser({ username: newUsername.trim() });
      setShowSettings(false);
    }
  };
  
  const handleTeamChangeClick = async (team) => {
    if (gameId && user && !isChangingTeam) {
      setIsChangingTeam(true);
      
      try {
        // Use the improved handleTeamChange from GameContext with gameId parameter
        if (handleTeamChange) {
          await handleTeamChange(gameId, team);
        } else {
          // Fallback to direct changeTeam if handleTeamChange not available
          await changeTeam(gameId, user.id, team);
          // Update local user state for immediate feedback
          updateUser({ team });
        }
      } catch (error) {
        console.error("Failed to change team:", error);
        // Force update the UI for better UX even if backend fails
        updateUser({ team });
      } finally {
        setIsChangingTeam(false);
        setShowSettings(false);
      }
    }
  };
  
  // Get display name for team with fallback handling
  const getTeamDisplay = () => {
    if (!user) return 'Observer';
    
    // Check team from user object or game state
    let team = user.team;
    if (!team && game?.players) {
      const player = game.players.find(p => p.id === user.id);
      if (player) {
        team = player.team;
      }
    }
    
    if (!team) return 'Observer';
    return team.charAt(0).toUpperCase() + team.slice(1);
  };
  
  return (
    <nav className={`navbar ${darkMode ? 'dark' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-brand">
          <Link to="/">
            <h1>Codenames</h1>
          </Link>
        </div>
        
        <div className="navbar-actions">
          <button 
            className="theme-toggle" 
            onClick={toggleDarkMode} 
            aria-label={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
          >
            {darkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
          
          {user && (
            <div className="user-menu">
              <button 
                className={`user-menu-button ${user.team || 'observer'}-user`}
                onClick={() => setShowSettings(!showSettings)}
              >
                <div className="user-info">
                  <span className="username">{user.username}</span>
                  <span className="team-label">{getTeamDisplay()}</span>
                </div>
                <span className="dropdown-icon">‚ñº</span>
              </button>
              
              {showSettings && (
                <div className="settings-dropdown">
                  {/* Change username */}
                  <form onSubmit={handleUsernameChange} className="settings-form">
                    <label htmlFor="username">Change Name</label>
                    <div className="input-group">
                      <input
                        type="text"
                        id="username"
                        value={newUsername}
                        onChange={(e) => setNewUsername(e.target.value)}
                        placeholder="New username"
                      />
                      <button type="submit">Save</button>
                    </div>
                  </form>
                  
                  {/* Change team */}
                  {gameId && (
                    <div className="team-selection">
                      <label>Change Team</label>
                      <div className="team-buttons">
                        <button 
                          className={`team-btn red ${user?.team === 'red' ? 'active' : ''}`}
                          onClick={() => handleTeamChangeClick('red')}
                          disabled={isChangingTeam}
                        >
                          Red Team
                        </button>
                        <button 
                          className={`team-btn blue ${user?.team === 'blue' ? 'active' : ''}`}
                          onClick={() => handleTeamChangeClick('blue')}
                          disabled={isChangingTeam}
                        >
                          Blue Team
                        </button>
                      </div>
                      {isChangingTeam && <div className="loading">Changing team...</div>}
                    </div>
                  )}
                  
                  {/* Close button */}
                  <button 
                    className="close-settings"
                    onClick={() => setShowSettings(false)}
                  >
                    Close
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
